    min-height: 80rem;
    margin: 0;
  }
  .user-input {
    flex-basis: 50%;

    max-width: 90%;
    justify-content: space-between;
    margin: 3rem;
  }
  .tip__form {
    display: grid;
    grid-gap: 1rem;
    width: 100%;
    justify-content: space-around;
    grid-template-areas:
      "tip__label tip__label"
      "tip__5 tip__10 "
      "tip__15 tip__25"
      "tip__50 tip__custom";
  }
  .tip {
    align-items: center;
    &__label {
      @include label-look;
    }
    &__5,
    &__10,
    &__15,
    &__25,
    &__50 {
      align-self: center;
      font-size: 3rem !important;
      min-width: 23rem;
      border-radius: 10rem;
    }
    &__custom {
      min-width: 23rem;
    }
  }

  .user-output {
    width: 80%;
    min-height: 35rem;
    &__amount {
      font-size: 3rem;
    }
    &__person {
      font-size: 2rem;
    }
  }
}
